  public static class DataBean implements Parcelable{

    /**
     * id_cat : 1
     * nama : Resep Mendol Tempe
     * foto : mendol.png
     * deskripsi : Bahan-bahan
     1 papan tempe
     2 batang daun bawang,iris
     Bumbu halus:
     4 butir bawang merah
     1 siung bawang putih
     1/2 sdt ketumbar
     2 lbr daun jeruk
     2 ruas kunyit
     3 buah cabe rawit
     Secukupnya gula & garam
     Sejumput penyedap/ kaldu bubuk bila suka

     Langkah
     1. Haluskan bumbu,kemudian masukkan tempe haluskan,campur hingga rata.
     2. Tambahkan daun bawang campur,koreksi rasa,bentuk bulat lonjong.
     3. Goreng dlm minyak panas hingga kuning kecoklatan angkat,sajikan.
     * id_kategori : 1
     */

    private String id_cat;
    private String nama;
    private String foto;
    private String deskripsi;
    private String id_kategori;

    protected DataBean(Parcel in) {
      id_cat = in.readString();
      nama = in.readString();
      foto = in.readString();
      deskripsi = in.readString();
      id_kategori = in.readString();
    }

    public static final Creator<DataBean> CREATOR = new Creator<DataBean>() {
      @Override
      public DataBean createFromParcel(Parcel in) {
        return new DataBean(in);
      }

      @Override
      public DataBean[] newArray(int size) {
        return new DataBean[size];
      }
    };

    public String getId_cat() {
      return id_cat;
    }

    public void setId_cat(String id_cat) {
      this.id_cat = id_cat;
    }

    public String getNama() {
      return nama;
    }

    public void setNama(String nama) {
      this.nama = nama;
    }

    public String getFoto() {
      return foto;
    }

    public void setFoto(String foto) {
      this.foto = foto;
    }

    public String getDeskripsi() {
      return deskripsi;
    }

    public void setDeskripsi(String deskripsi) {
      this.deskripsi = deskripsi;
    }

    public String getId_kategori() {
      return id_kategori;
    }

    public void setId_kategori(String id_kategori) {
      this.id_kategori = id_kategori;
    }

    @Override
    public int describeContents() {
      return 0;
    }

    @Override
    public void writeToParcel(Parcel parcel, int i) {
      parcel.writeString(id_cat);
      parcel.writeString(nama);
      parcel.writeString(foto);
      parcel.writeString(deskripsi);
      parcel.writeString(id_kategori);
    }
  }
